Wavetable::Wavetable(int sRate, int bSize,float freq)
{
  // No need to allocate memory for the buffer_ array, as it is static

  //initalise the table to the default table
  Wavetable::tablePtr = Wavetable::table0;

  //set the samplerate to be used for frequency calculation

    //figure out how to make this private
  Wavetable::sampleRate = sRate;
  Wavetable::blockSize = bSize;

    //initialise values
  Wavetable::bufferPos = 0;

  Wavetable::tableMorph = 0;
  Wavetable::setFreq(freq);
//   Wavetable::bufferPos = 0;
  
}

Wavetable::Wavetable() : sampleRate(48000), blockSize(4), freq(440) { }


